parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "firstName": { "type": "string" },
    "lastName": { "type": "string" },

    "age": { "type": "integer", "minimum": 0, "maximum": 120 },
    "gender": {
      "type": "string",
      "enum": ["Male", "Female", "Non-binary", "Other", "Prefer not to say"]
    },
    "race": {
      "type": "string",
      "enum": [
        "White",
        "Black or African American",
        "Asian",
        "American Indian or Alaska Native",
        "Native Hawaiian or Other Pacific Islander",
        "Other",
        "Prefer not to say"
      ]
    },
    "isHispanic": { "type": "boolean" },

    "location": {
      "type": "object",
      "properties": {
        "country": { "type": "string" },
        "state": { "type": "string" },
        "city": { "type": "string" }
      },
      "required": ["country"]
    },

    "employment": {
      "type": "object",
      "properties": {
        "jobType": {
          "type": "string",
          "enum": [
            "Full-time",
            "Part-time",
            "Unemployed",
            "Retired",
            "Self-employed",
            "Internship",
            "Contractor"
          ]
        },
        "title": { "type": "string" },
        "industry": { "type": "string" },
        "salary": { "type": "number", "minimum": 0 }
      }
    },

    "education": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "level": {
            "type": "string",
            "enum": [
              "High School",
              "Some College",
              "Associate's",
              "Bachelor's",
              "Master's",
              "Doctorate",
              "Vocational / Technical",
              "Other"
            ]
          },
          "fieldOfStudy": { "type": "string" },
          "institution": { "type": "string" },
          "yearCompleted": {
            "type": "integer",
            "minimum": 1900,
            "maximum": 2100
          }
        },
        "required": ["level", "fieldOfStudy", "institution", "yearCompleted"]
      }
    }
  },
  "required": [
    "firstName",
    "lastName",
    "age",
    "gender",
    "race",
    "isHispanic",
    "location",
    "employment",
    "education"
  ]
}

{
  "type": "object",
  "properties": {
    "answers": {
      "type": "array",
      "items": {
        "type": ["string", "array", "number", "boolean", "null"],
        "description": "The answer(s) chosen by the persona as a response to the question. Use null to skip an optional question"
      }
    }
  },
  "required": ["answers"]
}
